    	SmartDashboard.putNumber("gyroRate", rate);
    	SmartDashboard.putNumber("error", mError*kP);
    	SmartDashboard.putNumber("lastError", mLastError*kD);
    	SmartDashboard.putNumber("acumError", mAcumError*kI);    	
    	SmartDashboard.putNumber("speed", y);
    	SmartDashboard.putNumber("turning", turningValue);
    	SmartDashboard.putNumber("LeftEnc", driveBaseLeftMain.getSelectedSensorPosition(0));
    	SmartDashboard.putNumber("RightEnc", driveBaseRightMain.getSelectedSensorPosition(0));
    	SmartDashboard.putNumber("LeftVel", driveBaseLeftMain.getSelectedSensorVelocity(0));
